dataShapes.serializedZodSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "description": "DataShape: SimpleTextQuestion - Schema for card creation",
  "type": "object",
  "properties": {
    "questionText": {
      "type": "string",
      "minLength": 1
    },
    "correctAnswer": {
      "type": "string",
      "minLength": 1
    }
  },
  "required": [
    "questionText",
    "correctAnswer"
  ],
  "additionalProperties": false
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "description": "DataShape: MultipleChoiceQuestion - Schema for card creation",
  "type": "object",
  "properties": {
    "questionText": {
      "type": "string",
      "minLength": 1
    },
    "options": {
      "type": "string",
      "minLength": 1
    },
    "correctAnswer": {
      "type": "string",
      "minLength": 1
    }
  },
  "required": [
    "questionText",
    "options",
    "correctAnswer"
  ],
  "additionalProperties": false
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "description": "DataShape: NumberRangeQuestion - Schema for card creation",
  "type": "object",
  "properties": {
    "questionText": {
      "type": "string",
      "minLength": 1
    },
    "min": {
      "description": "Numeric value for min",
      "type": "number"
    },
    "max": {
      "description": "Numeric value for max",
      "type": "number"
    }
  },
  "required": [
    "questionText",
    "min",
    "max"
  ],
  "additionalProperties": false
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "description": "DataShape: Blanks - Schema for card creation",
  "type": "object",
  "properties": {
    "Input": {
      "description": "Markdown text with blanks. Format: {{answer}} for fill-in or {{answer1|answer2||distractor1|distractor2}} for multiple choice. Use the 'fill-in-card-authoring' MCP prompt for detailed syntax guidance.",
      "type": "string",
      "minLength": 1
    },
    "Uploads": {
      "description": "Optional media files (images, audio, etc.)",
      "type": "array",
      "items": {}
    }
  },
  "required": [
    "Input"
  ],
  "additionalProperties": false
}
